import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
const apiKey = "2888152ec00da94eef10fefe0d3dbb48"

function Ques(){
    const [question,setQuestion] = useState({});
    useEffect(() => {
        (async function() {
        const url = `http://www.career.go.kr/inspct/openapi/test/questions?apikey=${apiKey}&q=6`
        const response = await axios.get(url);
        setQuestion({q: response.data, answer1: response.data.RESULT[0].answer01, answer2: response.data.RESULT[0].answer02});})();
    },[])

    return (
        <div>
            <p>{question.RESULT[0].answer01}</p>
            <input type='radio' name='ans'/>{question.answer1}
            <input type='radio' name='ans'/>{question.answer2}

            <div>
                <button>다음</button>
            </div>
        </div>
    );
}

function Test() {
    return (
        <div className="Test">
            <Ques/>
        </div>
    );
}

export default Test;


