import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const apiKey = "2888152ec00da94eef10fefe0d3dbb48"
const [currentPage, setCurrentPage] = useState(1); 
function Ques(){
    const [questions,setQuestion] = useState([]);
    const lastPage = 6 //parseInt

    //const [currentPage, setCurrentPage] = useState(1); 

    useEffect(() => {
        (async function() {
        const url = `http://www.career.go.kr/inspct/openapi/test/questions?apikey=${apiKey}&q=6`
        const response = await axios.get(url);
        //setQuestion({qitemNo: response.data.RESULT[0].qitemNo ,q: response.data.RESULT[0].question, answer1: response.data.RESULT[0].answer01, answer2: response.data.RESULT[0].answer02});
        //setQuestion(response.datsa);
        setQuestion(response.data.RESULT);
        // setTotalData(response.data.RESULT.length);
        })();
    },[])
                //((questions.slice((currentPage - 1) * 5, currentPage * 5).map((questions) => {});

    return (
        <div>
            <h1>검사 진행</h1>
            
            <p>{questions.slice((currentPage - 1) * 5, currentPage * 5).map((q) => (
                <>
                    <p>{q.qitemNo}. {q.question}</p>
                    <input type='radio' name={q.qitemNo} value={q.answerScore01}/>{q.answer01}
                    <input type='radio' name={q.qitemNo} value={q.answerScore02}/>{q.answer02}
                </>
            ))}</p>


            <div>
                <button onClick={()=>{
                    if (currentPage > 1){
                        setCurrentPage(currentPage-1);    
                    }
                    }}>이전</button>
                <Buttn currentPage={currentPage} lastPage={lastPage} onClick={() => {
                    if (currentPage < lastPage){
                        setCurrentPage(currentPage + 1)
                    }
                }} />
                
            </div>
        </div>
    )
}
function Buttn(props) {
    if (props.currentPage < props.lastPage) {
      return (
        
        <button onClick={() => {setCurrentPage(currentPage-1)}}>다음</button>
      )
    } else {
      return (
        <Link to='/finished'>
          <button>제출</button>
        </Link> 
      )
    }
  }
function Test() {
    return (
        <div className="Test">
            <Ques/>
        </div>
    );
}

export default Test;
