import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
const apiKey = "2888152ec00da94eef10fefe0d3dbb48"

function Ques(){
    const [questions,setQuestion] = useState([]);
    //const { id } = useParams();
    //const state = useContext(createContext);
    useEffect(() => {
        (async function() {
        const url = `http://www.career.go.kr/inspct/openapi/test/questions?apikey=${apiKey}&q=6`
        const response = await axios.get(url);
        //setQuestion({qitemNo: response.data.RESULT[0].qitemNo ,q: response.data.RESULT[0].question, answer1: response.data.RESULT[0].answer01, answer2: response.data.RESULT[0].answer02});
        //setQuestion(response.datsa);
        setQuestion(response.data.RESULT);

        })();
    },[])

    return (
        <div>
            <h1>검사 진행</h1>
            <p>{questions[0]?.question}</p>
            <input type='radio' name='ans'/>{questions[0]?.answer01}
            <input type='radio' name='ans'/>{questions[0]?.answer02}
            <p>{questions[1]?.question}</p>
            <p>{questions[2]?.question}</p>
            <p>{questions[3]?.question}</p>
            <p>{questions[4]?.question}</p>
        </div>
    )
}

function Test() {
    return (
        <div className="Test">
            <Ques/>
        </div>
    );
}

export default Test;


