import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import { useHistory } from "react-router";

const apiKey = "2888152ec00da94eef10fefe0d3dbb48"

function Ques(){
    const [questions,setQuestion] = useState([]);
    const [scores, setScores] = useState({});
    const [currentPage, setCurrentPage] = useState(1); 
    const history = useHistory();
    const lastPage = 6 //parseInt
    const handleChange = (e) => setScores({...scores, [e.target.name]: e.target.value});
    useEffect(() => {
        (async function() {
        const url = `http://www.career.go.kr/inspct/openapi/test/questions?apikey=${apiKey}&q=6`
        const response = await axios.get(url);
        setQuestion(response.data.RESULT);
        })();
    },[])
    function Disselect() {
        $("input[name='q.qitemNo']").prop('checked', false)
    }
    return (
        <div>
            <h1>검사 진행</h1>
            <p>{questions.slice((currentPage - 1) * 5, currentPage * 5).map((q) => (
                <>
                    <p>{q.qitemNo}. {q.question}</p>
                    <input 
                        type='radio' 
                        name={q.qitemNo} 
                        value={q.answerScore01} 
                        onChange={handleChange}
                    />{q.answer01}
                    <input 
                        type='radio' 
                        name={q.qitemNo} 
                        value={q.answerScore02} 
                        onChange={handleChange}
                    />{q.answer02}
                </> 
            ))}</p>

            
            <div>
                <button onClick={()=>{
                    Disselect()
                    if (currentPage > 1){
                        setCurrentPage(currentPage-1);    
                    }
                    }}>이전</button>
                <button onClick={()=>{
                    if (currentPage < lastPage){
                        setCurrentPage(currentPage+1);    
                    }else{
                        history.push("/finished");
                    }
                    }}>{currentPage < lastPage ? "다음" : "제출"}</button>
                
            </div>
        </div>
    )
}


function Test() {
    return (
        <div className="Test">
            <Ques/>
        </div>
    );
}

export default Test;
